// Модальное окно сообщения
import PropTypes from 'prop-types';
import ButtonOk from './ButtonOk';

const Message = ({
  close, messageHead, messageText, ulData,
}) => (
  <>
    <div className="messageBG">
      <div className="message">
        <h1>{messageHead}</h1>
        <p>{messageText}</p>
        <ul>
          {ulData.map((item) => (
            <li key={item}>{item}</li>
          ))}
        </ul>
        <ButtonOk onClick={close} />
      </div>
    </div>
    <style jsx>
      {`
        .messageBG {
          width: 100vw;
          height: 100vh;
          position: absolute;
          background-color: rgba(0, 0, 0, 0.5);
        }

        .message {
          position: relative;
          overflow-y: auto;
          padding: 20px;
          width: 360px;
          height: 360px;
          text-align: center;
          background: url(images/note_transparent.svg) repeat-x top left / 36px auto,
            url(images/cel.svg);
          background-color: beige;
          border-top: none;
          top: 200px;
          margin: auto;
        }
      `}
    </style>
  </>
);

Message.propTypes = {
  close: PropTypes.func,
  messageHead: PropTypes.string,
  messageText: PropTypes.string,
  ulData: PropTypes.arrayOf(PropTypes.string),
};

Message.defaultProps = {
  close: () => {},
  messageHead: '',
  messageText: '',
  ulData: [],
};

export default Message;
