import { useState } from 'react';
import ReactDOM from 'react-dom';
import PropTypes from 'prop-types';

const useModal = (WpapModal) => {
  const [visiblity, setVisiblity] = useState(false);
  const openModalWindow = () => setVisiblity(true);
  const closeModalWindow = () => setVisiblity(false);
  const Modal = (props) => {
    if (!visiblity || document === undefined) return null;
    return ReactDOM.createPortal(<WpapModal {...props} />, document.querySelector('body'));
  };
  return [Modal, openModalWindow, closeModalWindow];
};

// useModal.propTypes = {
