// Модальное окно сообщения
import ReactDOM from 'react-dom';
import PropTypes from 'prop-types';
import ButtonOk from './ButtonOk';
import connector from '../../Store/Connector';

const Message = ({
  messageShowing, messageText, messageHead, messageUlData, closeMessage,
}) => {
  if (messageShowing) return null;
  return ReactDOM.createPortal(
    <div className="messageBG">
      <div className="message">
        <h1>{messageHead}</h1>
        <p>{messageText}</p>

        <ul>
          {messageUlData.map((item) => (
            <li>{item}</li>
          ))}
        </ul>
        <ButtonOk onClick={closeMessage} />
      </div>
    </div>,
    document.querySelector('body'),
  );
};

Message.propTypes = {
  messageShowing: PropTypes.boolean,
  messageHead: PropTypes.string,
  messageText: PropTypes.string,
  messageUlData: PropTypes.arrayOf(PropTypes.string),
  closeMessage: PropTypes.func,
};

Message.defaultProps = {
  messageShowing: false,
  messageHead: '',
  messageText: '',
  messageUlData: [],
  closeMessage: () => {},
};

export default connector(Message);
